<style>
	svg {
		margin-right: 35px;
	}

	.mobile--mute {
		margin-left: auto;
		padding-right: 35px;
	}
	nav {
		height: 45px;
		min-height: 105px;
		padding: 35px;
		width: 100%;
		border-bottom: 1px solid var(--black-color);
		font-size: 1.6em;
	}

	.inner {
		max-width: 980px;
		box-sizing: border-box;
		display: flex;
		align-items: center;
		justify-content: space-between;
		height: 100%;
	}

	.mobile-icon {
		width: 25px;
		height: 14px;
		position: relative;
		cursor: pointer;
	}

	.mobile-icon:after,
	.mobile-icon:before,
	.middle-line {
		content: '';
		position: absolute;
		width: 100%;
		height: 2px;
		background-color: var(--black-color);
		transition: all 0.4s;
		transform-origin: center;
	}

	.mobile-icon:before,
	.middle-line {
		top: 0;
		right: 0;
	}

	.mobile-icon:after,
	.middle-line {
		bottom: 0;
	}

	.mobile-icon:before {
		width: 66%;
	}

	.mobile-icon:after {
		width: 33%;
	}

	.middle-line {
		margin: auto;
	}

	.mobile-icon:hover:before,
	.mobile-icon:hover:after,
	.mobile-icon.active:before,
	.mobile-icon.active:after,
	.mobile-icon.active .middle-line {
		width: 100%;
	}

	.mobile-icon.active:before,
	.mobile-icon.active:after {
		top: 50%;
		transform: rotate(-45deg);
	}

	.mobile-icon.active .middle-line {
		transform: rotate(45deg);
	}

	.navbar-list {
		display: none;
		width: 100%;
		justify-content: space-between;
		margin: 0;
		padding: 0 40px;
	}

	.navbar-list.mobile {
		background-color: rgba(0, 0, 0, 0.9);
		position: fixed;
		display: block;
		height: 100%;
		bottom: 0;
		left: 0;
		color: white;
		padding: 2rem;
	}

	.navbar-list.mobile a {
		color: white;
	}

	.navbar-list li {
		list-style-type: none;
		position: relative;
	}

	.navbar-list li:before {
		content: '';
		position: absolute;
		bottom: 0;
		left: 0;
		width: 100%;
		height: 1px;
	}

	.navbar-list a {
		color: var(--black-color);
		text-decoration: none;
		display: flex;
		align-items: center;
		padding: 20px;
		border-radius: 50px;
		border: 2px solid var(--black-color);
		font-size: 1.2em;
		margin-left: 35px;
		transition: all 1s;
	}
	.navbar-list a:hover {
		background-color: var(--purple-color);
	}

	.navbar-list a.active {
		background-color: var(--purple-color);
	}

	@media only screen and (min-width: 767px) {
		.mobile-icon {
			display: none;
		}

		.navbar-list {
			display: flex;
			padding: 0;
			align-items: center;
		}

		.navbar-list a {
			display: inline-flex;
		}

		.mobile--mute {
			display: none;
		}
	}
</style>
